"use client";

import * as React from "react";
import { cn } from "@/lib/utils";
import {
  Breadcrumb,
  BreadcrumbItem,
  BreadcrumbLink,
  BreadcrumbList,
  BreadcrumbPage,
  BreadcrumbSeparator,
} from "@/components/ui/breadcrumb";

/**
 * Header component for creating flexible headers that can contain controls for panels.
 *
 * Features:
 * - Flexible layout with left and right content areas
 * - Built-in breadcrumb support
 * - Button and control placement on the right
 * - Consistent styling with design system
 * - Responsive design
 *
 * @example
 * ```tsx
 * <Header
 *   breadcrumbs={[
 *     { label: "Home", href: "/" },
 *     { label: "Weather Dashboard", href: "/dashboard" }
 *   ]}
 *   rightContent={<Button>Secondary</Button>}
 * />
 * ```
 */

interface BreadcrumbItem {
  label: string;
  href?: string;
}

interface HeaderProps {
  /** Breadcrumb navigation items */
  breadcrumbs?: BreadcrumbItem[];
  /** Content to display on the left side (overrides breadcrumbs) */
  leftContent?: React.ReactNode;
  /** Content to display on the right side (buttons, controls, etc.) */
  rightContent?: React.ReactNode;
  /** Additional CSS classes */
  className?: string;
}

/**
 * Header component for creating flexible headers with breadcrumbs and controls.
 *
 * @param props - Header component props
 * @returns JSX element
 */
export function Header({
  breadcrumbs,
  leftContent,
  rightContent,
  className,
  ...props
}: HeaderProps) {
  return (
    <header
      className={cn(
        "flex w-full h-12 items-center justify-between border-b border-border bg-background px-3 py-2 shadow-sm",
        className
      )}
      {...props}
    >
      {/* Left side content */}
      <div className="flex items-center">
        {leftContent ? (
          leftContent
        ) : breadcrumbs ? (
          <Breadcrumb>
            <BreadcrumbList>
              {breadcrumbs.map((item, index) => (
                <React.Fragment key={index}>
                  {index > 0 && <BreadcrumbSeparator />}
                  <BreadcrumbItem>
                    {item.href ? (
                      <BreadcrumbLink href={item.href}>
                        {item.label}
                      </BreadcrumbLink>
                    ) : (
                      <BreadcrumbPage>{item.label}</BreadcrumbPage>
                    )}
                  </BreadcrumbItem>
                </React.Fragment>
              ))}
            </BreadcrumbList>
          </Breadcrumb>
        ) : null}
      </div>

      {/* Right side content */}
      {rightContent && (
        <div className="flex items-center space-x-2">
          {rightContent}
        </div>
      )}
    </header>
  );
}

/**
 * Three Panel Layout Page Component
 *
 * A layout with three side-by-side panels:
 * - BrandSidebar for navigation
 * - Header with custom left content for navigation
 * - Left panel: Fixed width, max 280px with clean panel structure
 * - Center panel: Flexible width with clean panel structure
 * - Right panel: Fixed width, max 280px with clean panel structure
 * - Responsive design - panels stack vertically on mobile
 * - Uses Panel component from the design system
 *
 * This component provides a flexible three-panel layout structure
 * perfect for applications with sidebar navigation, main content area,
 * and a details/settings panel on the right.
 *
 * @returns JSX element with three-panel layout
 */
import { BrandSidebar } from "@/components/brand-sidebar";
import { Panel } from "@/components/panel";
import { Button } from "@/components/ui/button";
import { Header } from "@/components/ui/header";
import { SidebarProvider } from "@/components/ui/sidebar";
import { Inbox, Settings } from "lucide-react";

export default function ThreePanelLayoutPage() {
  return (
    <SidebarProvider>
      <BrandSidebar />
      <main className="flex-1 flex flex-col">
        {/* Header with custom left content */}
        <Header
        leftContent={
          <div className="flex items-center gap-3">
            <Button variant="ghost" size="icon">
              <Inbox className="w-4 h-4" />
            </Button>
            <h6 className="font-medium">Three Panel Dashboard</h6>
          </div>
        }
        rightContent={
          <div className="flex items-center space-x-2">
            <Button variant="ghost" size="icon">
              <Settings className="w-4 h-4" />
            </Button>
          </div>
        }
      />

      {/* Three Panel Layout */}
      <div className="flex flex-col md:flex-row flex-1">
        {/* Left Panel - Fixed width, max 280px on desktop, full width on mobile */}
        <Panel
          size="fit"
          responsive={{ md: "fit" }}
          title="Left Panel"
          className="w-full md:max-w-[280px] md:w-[280px]"
          footer={
            <span className="text-sm text-muted-foreground">
              Left Panel Footer
            </span>
          }
        >
          {/* Panel content area - ready for your content */}
        </Panel>

        {/* Center Panel - Flexible width on desktop, full width on mobile */}
        <Panel
          size="full"
          responsive={{ md: "full" }}
          title="Main Content"
          className="w-full flex-1"
          footer={
            <span className="text-sm text-muted-foreground">
              Main Content Footer
            </span>
          }
        >
          {/* Panel content area - ready for your content */}
        </Panel>

        {/* Right Panel - Fixed width, max 280px on desktop, full width on mobile */}
        <Panel
          size="fit"
          responsive={{ md: "fit" }}
          title="Right Panel"
          className="w-full md:max-w-[280px] md:w-[280px]"
          borderRight={false}
          footer={
            <span className="text-sm text-muted-foreground">
              Right Panel Footer
            </span>
          }
        >
          {/* Panel content area - ready for your content */}
        </Panel>
      </div>
      </main>
    </SidebarProvider>
  );
}

"use client";

import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";
import type * as React from "react";
// Removed external component imports for v0 compatibility

/**
 * Panel component for creating flexible layouts with header, content, and footer areas.
 *
 * Features:
 * - Responsive sizing with flexible column system
 * - Header with title and action button
 * - Content area with proper spacing
 * - Footer with status information
 * - Customizable alignment and justification
 * - Border and styling options
 *
 * @example
 * ```tsx
 * // Basic panel with default 100% height (fills parent container)
 * <Panel size="4" title="My Panel" footer={<span>Status: Active</span>}>
 *   <div>Panel content goes here</div>
 * </Panel>
 *
 * // Panel with full screen height (100vh)
 * <Panel height="screen" title="My Panel">
 *   <div>Panel content goes here</div>
 * </Panel>
 *
 * // Panel with custom header and full height
 * <Panel height="full" header={
 *   <div className="flex items-center justify-between w-full">
 *     <h6>Custom Header</h6>
 *     <Button size="sm">Action</Button>
 *   </div>
 * }>
 *   <div>Panel content with custom header</div>
 * </Panel>
 *
 * // Panel with responsive sizing and full height
 * <Panel
 *   size="6"
 *   height="full"
 *   responsive={{ md: "4", lg: "3" }}
 *   title="Responsive Panel"
 * >
 *   <div>Content adapts to screen size</div>
 * </Panel>
 * ```
 */

type ColumnSize =
  | "auto"
  | "1"
  | "2"
  | "3"
  | "4"
  | "5"
  | "6"
  | "12"
  | "full"
  | "fit";

/**
 * Props for the Panel component
 */
interface PanelProps {
  /** Sizing */
  /** Panel size using flexible column system */
  size?: ColumnSize;
  /** Responsive sizing for different breakpoints */
  responsive?: {
    sm?: ColumnSize;
    md?: ColumnSize;
    lg?: ColumnSize;
    xl?: ColumnSize;
  };

  /** Layout */
  /** Vertical alignment of panel content */
  align?: "start" | "center" | "end" | "stretch";
  /** Horizontal justification of panel content */
  justify?: "start" | "center" | "end" | "between" | "around" | "evenly";

  /** Content areas */
  /** Panel title displayed in header */
  title?: string;
  /** Custom header content (overrides title) */
  header?: React.ReactNode;
  /** Whether to hide the header completely */
  hideHeader?: boolean;
  /** Main panel content */
  children?: React.ReactNode;
  /** Footer content with status information */
  footer?: React.ReactNode;
  /** Whether to hide the footer completely */
  hideFooter?: boolean;

  /** Styling */
  /** Whether to show right border */
  borderRight?: boolean;
  /** Height behavior: 'screen' for 100vh, 'full' for 100% of parent */
  height?: "screen" | "full";
  /** Additional CSS classes */
  className?: string;
}

const sizeClasses = {
  auto: "flex-auto",
  "1": "flex-[0_0_8.333333%]",
  "2": "flex-[0_0_16.666667%]",
  "3": "flex-[0_0_25%]",
  "4": "flex-[0_0_33.333333%]",
  "5": "flex-[0_0_41.666667%]",
  "6": "flex-[0_0_50%]",
  "12": "flex-[0_0_100%]",
  full: "flex-1",
  fit: "flex-none",
};

const alignClasses = {
  start: "items-start",
  center: "items-center",
  end: "items-end",
  stretch: "items-stretch",
};

const justifyClasses = {
  start: "justify-start",
  center: "justify-center",
  end: "justify-end",
  between: "justify-between",
  around: "justify-around",
  evenly: "justify-evenly",
};

const getResponsiveClasses = (responsive?: PanelProps["responsive"]) => {
  if (!responsive) return "";

  const classes: string[] = [];

  if (responsive.sm) classes.push(`sm:${sizeClasses[responsive.sm]}`);
  if (responsive.md) classes.push(`md:${sizeClasses[responsive.md]}`);
  if (responsive.lg) classes.push(`lg:${sizeClasses[responsive.lg]}`);
  if (responsive.xl) classes.push(`xl:${sizeClasses[responsive.xl]}`);

  return classes.join(" ");
};

/**
 * Panel component for creating flexible layouts with header, content, and footer areas.
 *
 * @param props - Panel component props
 * @returns JSX element
 */
export function Panel({
  size = "auto",
  responsive,
  align = "stretch",
  justify = "start",
  title = "Panel",
  header,
  hideHeader = false,
  children,
  footer,
  hideFooter = false,
  borderRight = true,
  height = "full",
  className,
  ...props
}: PanelProps) {
  const baseSizeClass = sizeClasses[size];
  const responsiveClasses = getResponsiveClasses(responsive);

  // Determine height class based on height prop
  const heightClass = height === "screen" ? "h-screen" : "h-full";

  return (
    <div
      className={cn(
        "relative flex flex-col",
        heightClass,
        borderRight && "border-r border-border",
        baseSizeClass,
        responsiveClasses,
        alignClasses[align],
        justifyClasses[justify],
        className,
      )}
      {...props}
    >
      {!hideHeader && (
        <div className="absolute top-0 left-0 right-0 z-10 flex w-full h-12 px-3 py-2 justify-between items-center border-b border-border bg-background shadow-sm">
          {header ? (
            header
          ) : (
            <>
              <h6>{title}</h6>
              <Button variant="ghost" size="icon">
                <svg
                  className="size-4"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M12 5v.01M12 12v.01M12 19v.01M12 6a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2z"
                  />
                </svg>
              </Button>
            </>
          )}
        </div>
      )}

      {children && (
        <div
          className={cn(
            "flex-1 w-full p-3 items-start self-stretch bg-background overflow-y-auto",
            !hideHeader && "pt-12", // 48px for header + 12px spacing
            !hideHeader && "pt-[60px]", // 48px for header + 12px spacing
            hideHeader && "pt-0", // No top padding when header is hidden
            !hideFooter && footer && "pb-12", // Bottom padding only when footer exists and not hidden
            hideFooter && "pb-0", // No bottom padding when footer is hidden
          )}
        >
          {children}
        </div>
      )}

      {!hideFooter && footer && (
        <div className="absolute bottom-0 left-0 right-0 z-10 flex w-full h-12 min-h-10 px-3 py-2 justify-between items-center border-t border-border bg-background text-muted-foreground text-sm">
          {footer}
        </div>
      )}
    </div>
  );
}
