import { Geist_Mono, Inter, Montserrat } from "next/font/google";
import React, { type ReactNode } from "react";

import { cn } from "@/lib/utils";

import "@/app/globals.css";

const InterSans = Inter({
  subsets: ["latin"],
  variable: "--font-sans",
});

const GeistMono = Geist_Mono({
  subsets: ["latin"],
  variable: "--font-mono",
});

const MontserratSerif = Montserrat({
  subsets: ["latin"],
  variable: "--font-serif",
});

export default function ShellLayout({
  children,
}: Readonly<{
  children: ReactNode;
}>) {
  return (
    <html
      lang="en"
      className={cn(
        InterSans.variable,
        GeistMono.variable,
        MontserratSerif.variable,
        "bg-background text-foreground",
      )}
    >
      <body>{children}</body>
    </html>
  );
}

"use client";

import {
  AudioWaveform,
  BookOpen,
  Bot,
  ChevronRight,
  ChevronsUpDown,
  Command,
  Frame,
  GalleryVerticalEnd,
  LogOut,
  Map,
  PieChart,
  Settings2,
  SquareTerminal,
} from "lucide-react";
import * as React from "react";

import { Logo } from "@/components/logo";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import {
  Collapsible,
  CollapsibleContent,
  CollapsibleTrigger,
} from "@/components/ui/collapsible";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuGroup,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { LogoContainer } from "@/components/ui/logo-container";
import {
  Sidebar,
  SidebarContent,
  SidebarFooter,
  SidebarGroup,
  SidebarGroupLabel,
  SidebarHeader,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarMenuSub,
  SidebarMenuSubButton,
  SidebarMenuSubItem,
  SidebarRail,
} from "@/components/ui/sidebar";

/**
 * Props for the BrandSidebar component
 */
interface BrandSidebarProps {
  /** Additional CSS classes to apply to the sidebar container */
  className?: string;
  /** Force the team switcher to be visible even with single team (for testing/admin) */
  forceTeamSwitcherVisible?: boolean;
}

// This is sample data.
// QA: To test single team behavior, change teams array to have only one item:
// teams: [{ name: "Acme Inc", logo: GalleryVerticalEnd, plan: "Enterprise" }]
const data = {
  user: {
    name: "shadcn",
    email: "m@example.com",
    avatar: "/avatars/shadcn.jpg",
  },
  teams: [
    {
      name: "DTN",
      logo: Logo,
      plan: "Enterprise",
    },
    {
      name: "Acme Corp.",
      logo: AudioWaveform,
      plan: "Startup",
    },
    {
      name: "Evil Corp.",
      logo: Command,
      plan: "Free",
    },
  ],
  navMain: [
    {
      title: "Playground",
      url: "#",
      icon: SquareTerminal,
      isActive: true,
      items: [
        {
          title: "History",
          url: "#",
        },
        {
          title: "Starred",
          url: "#",
        },
        {
          title: "Settings",
          url: "#",
        },
      ],
    },
    {
      title: "Models",
      url: "#",
      icon: Bot,
      items: [
        {
          title: "Genesis",
          url: "#",
        },
        {
          title: "Explorer",
          url: "#",
        },
        {
          title: "Quantum",
          url: "#",
        },
      ],
    },
    {
      title: "Documentation",
      url: "#",
      icon: BookOpen,
      items: [
        {
          title: "Introduction",
          url: "#",
        },
        {
          title: "Get Started",
          url: "#",
        },
        {
          title: "Tutorials",
          url: "#",
        },
        {
          title: "Changelog",
          url: "#",
        },
      ],
    },
    {
      title: "Settings",
      url: "#",
      icon: Settings2,
      items: [
        {
          title: "General",
          url: "#",
        },
        {
          title: "Team",
          url: "#",
        },
        {
          title: "Billing",
          url: "#",
        },
        {
          title: "Limits",
          url: "#",
        },
      ],
    },
  ],
  projects: [
    {
      name: "Design Engineering",
      url: "#",
      icon: Frame,
    },
    {
      name: "Sales & Marketing",
      url: "#",
      icon: PieChart,
    },
    {
      name: "Travel",
      url: "#",
      icon: Map,
    },
  ],
};

/**
 * BrandSidebar - A navigation sidebar component
 *
 * Based on the shadcn/ui sidebar-07 example that collapses to icons.
 * Features workspace switcher, collapsible nav sections, and user menu.
 *
 * IMPORTANT: Must be wrapped in SidebarProvider
 *
 * @example
 * ```tsx
 * import { SidebarProvider } from "@/components/ui/sidebar";
 * import { BrandSidebar } from "@/components/brand-sidebar";
 *
 * <SidebarProvider>
 *   <BrandSidebar />
 *   <main>
 *     <h1>Main Content</h1>
 *   </main>
 * </SidebarProvider>
 * ```
 */
export function BrandSidebar({
  className,
  forceTeamSwitcherVisible = false,
}: BrandSidebarProps) {
  const [activeTeam, setActiveTeam] = React.useState(data.teams[0]);

  // Determine if team switcher should be visible
  const hasMultipleTeams = data.teams.length > 1;
  const showTeamSwitcher = hasMultipleTeams || forceTeamSwitcherVisible;

  return (
    <Sidebar collapsible="icon" className={className}>
      <SidebarHeader className="border-b border-sidebar-border">
        <SidebarMenu>
          <SidebarMenuItem>
            {showTeamSwitcher ? (
              // Full dropdown menu for multiple teams or forced visibility
              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <SidebarMenuButton
                    size="lg"
                    className="data-[state=open]:bg-sidebar-accent data-[state=open]:text-sidebar-accent-foreground"
                  >
                    <LogoContainer logo={activeTeam.logo} className="size-8" />
                    <div className="grid flex-1 text-left text-sm leading-tight">
                      <span className="truncate font-semibold">
                        {activeTeam.name}
                      </span>
                      <span className="truncate text-xs">
                        {activeTeam.plan}
                      </span>
                    </div>
                    <ChevronsUpDown className="ml-auto group-data-[collapsible=icon]:hidden" />
                  </SidebarMenuButton>
                </DropdownMenuTrigger>
                <DropdownMenuContent
                  className="w-[--radix-dropdown-menu-trigger-width] min-w-56 rounded-lg"
                  align="start"
                  side="bottom"
                  sideOffset={4}
                >
                  <DropdownMenuLabel className="text-xs text-muted-foreground">
                    Teams
                  </DropdownMenuLabel>
                  {data.teams.map((team, index) => (
                    <DropdownMenuItem
                      key={team.name}
                      onClick={() => setActiveTeam(team)}
                      className="gap-2 p-2"
                    >
                      <div className="flex size-6 items-center justify-center rounded-sm border">
                        <LogoContainer logo={team.logo} />
                      </div>
                      {team.name}
                    </DropdownMenuItem>
                  ))}
                </DropdownMenuContent>
              </DropdownMenu>
            ) : (
              // Static display for single team (no dropdown functionality)
              <SidebarMenuButton
                size="lg"
                tooltip={`${activeTeam.name} - ${activeTeam.plan}`}
              >
                <LogoContainer logo={activeTeam.logo} className="size-8" />
                <div className="grid flex-1 text-left text-sm leading-tight group-data-[collapsible=icon]:hidden">
                  <span className="truncate font-semibold">
                    {activeTeam.name}
                  </span>
                  <span className="truncate text-xs">{activeTeam.plan}</span>
                </div>
                {/* No chevron for single team - static display only */}
              </SidebarMenuButton>
            )}
          </SidebarMenuItem>
        </SidebarMenu>
      </SidebarHeader>

      <SidebarContent>
        <SidebarGroup>
          <SidebarGroupLabel>Platform</SidebarGroupLabel>
          <SidebarMenu>
            {data.navMain.map((item) => (
              <Collapsible
                key={item.title}
                asChild
                defaultOpen={item.isActive}
                className="group/collapsible"
              >
                <SidebarMenuItem>
                  <CollapsibleTrigger asChild>
                    <SidebarMenuButton tooltip={item.title}>
                      {item.icon && <item.icon />}
                      <span>{item.title}</span>
                      <ChevronRight className="ml-auto transition-transform duration-200 group-data-[state=open]/collapsible:rotate-90" />
                    </SidebarMenuButton>
                  </CollapsibleTrigger>
                  <CollapsibleContent>
                    <SidebarMenuSub>
                      {item.items?.map((subItem) => (
                        <SidebarMenuSubItem key={subItem.title}>
                          <SidebarMenuSubButton asChild>
                            <a href={subItem.url}>
                              <span>{subItem.title}</span>
                            </a>
                          </SidebarMenuSubButton>
                        </SidebarMenuSubItem>
                      ))}
                    </SidebarMenuSub>
                  </CollapsibleContent>
                </SidebarMenuItem>
              </Collapsible>
            ))}
          </SidebarMenu>
        </SidebarGroup>
        <SidebarGroup className="group-data-[collapsible=icon]:hidden">
          <SidebarGroupLabel>Projects</SidebarGroupLabel>
          <SidebarMenu>
            {data.projects.map((item) => (
              <SidebarMenuItem key={item.name}>
                <SidebarMenuButton asChild>
                  <a href={item.url}>
                    <item.icon />
                    <span>{item.name}</span>
                  </a>
                </SidebarMenuButton>
              </SidebarMenuItem>
            ))}
          </SidebarMenu>
        </SidebarGroup>
      </SidebarContent>

      <SidebarFooter className="border-t border-sidebar-border">
        <SidebarMenu>
          <SidebarMenuItem>
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <SidebarMenuButton
                  size="lg"
                  className="data-[state=open]:bg-sidebar-accent data-[state=open]:text-sidebar-accent-foreground"
                >
                  <Avatar className="h-8 w-8">
                    <AvatarImage src={data.user.avatar} alt={data.user.name} />
                    <AvatarFallback>CN</AvatarFallback>
                  </Avatar>
                  <div className="grid flex-1 text-left text-sm leading-tight">
                    <span className="truncate font-semibold">
                      {data.user.name}
                    </span>
                    <span className="truncate text-xs">{data.user.email}</span>
                  </div>
                  <ChevronsUpDown className="ml-auto size-4" />
                </SidebarMenuButton>
              </DropdownMenuTrigger>
              <DropdownMenuContent
                className="w-[--radix-dropdown-menu-trigger-width] min-w-56 rounded-lg"
                side="bottom"
                align="end"
                sideOffset={4}
              >
                <DropdownMenuLabel className="p-0 font-normal">
                  <div className="flex items-center gap-2 px-1 py-1.5 text-left text-sm">
                    <Avatar className="h-8 w-8">
                      <AvatarImage
                        src={data.user.avatar}
                        alt={data.user.name}
                      />
                      <AvatarFallback>CN</AvatarFallback>
                    </Avatar>
                    <div className="grid flex-1 text-left text-sm leading-tight">
                      <span className="truncate font-semibold">
                        {data.user.name}
                      </span>
                      <span className="truncate text-xs">
                        {data.user.email}
                      </span>
                    </div>
                  </div>
                </DropdownMenuLabel>
                <DropdownMenuSeparator />
                <DropdownMenuGroup>
                  <DropdownMenuItem>
                    <Settings2 />
                    Account
                  </DropdownMenuItem>
                </DropdownMenuGroup>
                <DropdownMenuSeparator />
                <DropdownMenuItem>
                  <LogOut />
                  Log out
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </SidebarMenuItem>
        </SidebarMenu>
      </SidebarFooter>
      <SidebarRail />
    </Sidebar>
  );
}

"use client";

import * as React from "react";
import { cn } from "@/lib/utils";
import {
  Breadcrumb,
  BreadcrumbItem,
  BreadcrumbLink,
  BreadcrumbList,
  BreadcrumbPage,
  BreadcrumbSeparator,
} from "@/components/ui/breadcrumb";

/**
 * Header component for creating flexible headers that can contain controls for panels.
 *
 * Features:
 * - Flexible layout with left and right content areas
 * - Built-in breadcrumb support
 * - Button and control placement on the right
 * - Consistent styling with design system
 * - Responsive design
 *
 * @example
 * ```tsx
 * <Header
 *   breadcrumbs={[
 *     { label: "Home", href: "/" },
 *     { label: "Weather Dashboard", href: "/dashboard" }
 *   ]}
 *   rightContent={<Button>Secondary</Button>}
 * />
 * ```
 */

interface BreadcrumbItem {
  label: string;
  href?: string;
}

interface HeaderProps {
  /** Breadcrumb navigation items */
  breadcrumbs?: BreadcrumbItem[];
  /** Content to display on the left side (overrides breadcrumbs) */
  leftContent?: React.ReactNode;
  /** Content to display on the right side (buttons, controls, etc.) */
  rightContent?: React.ReactNode;
  /** Additional CSS classes */
  className?: string;
}

/**
 * Header component for creating flexible headers with breadcrumbs and controls.
 *
 * @param props - Header component props
 * @returns JSX element
 */
export function Header({
  breadcrumbs,
  leftContent,
  rightContent,
  className,
  ...props
}: HeaderProps) {
  return (
    <header
      className={cn(
        "flex w-full h-12 items-center justify-between border-b border-border bg-background px-3 py-2 shadow-sm",
        className
      )}
      {...props}
    >
      {/* Left side content */}
      <div className="flex items-center">
        {leftContent ? (
          leftContent
        ) : breadcrumbs ? (
          <Breadcrumb>
            <BreadcrumbList>
              {breadcrumbs.map((item, index) => (
                <React.Fragment key={index}>
                  {index > 0 && <BreadcrumbSeparator />}
                  <BreadcrumbItem>
                    {item.href ? (
                      <BreadcrumbLink href={item.href}>
                        {item.label}
                      </BreadcrumbLink>
                    ) : (
                      <BreadcrumbPage>{item.label}</BreadcrumbPage>
                    )}
                  </BreadcrumbItem>
                </React.Fragment>
              ))}
            </BreadcrumbList>
          </Breadcrumb>
        ) : null}
      </div>

      {/* Right side content */}
      {rightContent && (
        <div className="flex items-center space-x-2">
          {rightContent}
        </div>
      )}
    </header>
  );
}

/**
 * Two Panel Layout Page Component
 *
 * A layout with two side-by-side panels:
 * - BrandSidebar for navigation
 * - Header with breadcrumbs for navigation
 * - Left panel: max 320px width with clean panel structure
 * - Right panel: 70% width with clean panel structure
 * - Responsive design - panels stack vertically on mobile
 * - Uses Panel component from the design system
 *
 * This component provides a flexible two-panel layout structure
 * that adapts to different screen sizes and content needs.
 *
 * @returns JSX element with two-panel layout
 */
import { BrandSidebar } from "@/components/brand-sidebar";
import { Panel } from "@/components/panel";
import { Button } from "@/components/ui/button";
import { Header } from "@/components/ui/header";
import { SidebarProvider } from "@/components/ui/sidebar";

export default function TwoPanelLayoutPage() {
  return (
    <SidebarProvider>
      <BrandSidebar />
      <main className="flex-1 flex flex-col">
        {/* Header with breadcrumbs */}
        <Header
        breadcrumbs={[
          { label: "Home", href: "/" },
          { label: "Weather Dashboard" },
        ]}
        rightContent={<Button variant="outline">Settings</Button>}
      />

      {/* Two Panel Layout */}
      <div className="flex flex-col md:flex-row flex-1">
        {/* Left Panel - max 320px width on desktop, full width on mobile */}
        <Panel
          size="fit"
          responsive={{ md: "fit" }}
          title="Left Panel"
          className="w-full md:max-w-[320px] md:w-[30%] h-full"
          footer={
            <span className="text-sm text-muted-foreground">
              Left Panel Footer
            </span>
          }
        >
          {/* Panel content area - ready for your content */}
        </Panel>

        {/* Right Panel - 70% width on desktop, full width on mobile */}
        <Panel
          size="full"
          responsive={{ md: "full" }}
          title="Right Panel"
          className="w-full md:w-[70%] h-full"
          borderRight={false}
          footer={
            <span className="text-sm text-muted-foreground">
              Right Panel Footer
            </span>
          }
        >
          {/* Panel content area - ready for your content */}
        </Panel>
      </div>
      </main>
    </SidebarProvider>
  );
}

"use client";

import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";
import type * as React from "react";
// Removed external component imports for v0 compatibility

/**
 * Panel component for creating flexible layouts with header, content, and footer areas.
 *
 * Features:
 * - Responsive sizing with flexible column system
 * - Header with title and action button
 * - Content area with proper spacing
 * - Footer with status information
 * - Customizable alignment and justification
 * - Border and styling options
 *
 * @example
 * ```tsx
 * // Basic panel with default 100% height (fills parent container)
 * <Panel size="4" title="My Panel" footer={<span>Status: Active</span>}>
 *   <div>Panel content goes here</div>
 * </Panel>
 *
 * // Panel with full screen height (100vh)
 * <Panel height="screen" title="My Panel">
 *   <div>Panel content goes here</div>
 * </Panel>
 *
 * // Panel with custom header and full height
 * <Panel height="full" header={
 *   <div className="flex items-center justify-between w-full">
 *     <h6>Custom Header</h6>
 *     <Button size="sm">Action</Button>
 *   </div>
 * }>
 *   <div>Panel content with custom header</div>
 * </Panel>
 *
 * // Panel with responsive sizing and full height
 * <Panel
 *   size="6"
 *   height="full"
 *   responsive={{ md: "4", lg: "3" }}
 *   title="Responsive Panel"
 * >
 *   <div>Content adapts to screen size</div>
 * </Panel>
 * ```
 */

type ColumnSize =
  | "auto"
  | "1"
  | "2"
  | "3"
  | "4"
  | "5"
  | "6"
  | "12"
  | "full"
  | "fit";

/**
 * Props for the Panel component
 */
interface PanelProps {
  /** Sizing */
  /** Panel size using flexible column system */
  size?: ColumnSize;
  /** Responsive sizing for different breakpoints */
  responsive?: {
    sm?: ColumnSize;
    md?: ColumnSize;
    lg?: ColumnSize;
    xl?: ColumnSize;
  };

  /** Layout */
  /** Vertical alignment of panel content */
  align?: "start" | "center" | "end" | "stretch";
  /** Horizontal justification of panel content */
  justify?: "start" | "center" | "end" | "between" | "around" | "evenly";

  /** Content areas */
  /** Panel title displayed in header */
  title?: string;
  /** Custom header content (overrides title) */
  header?: React.ReactNode;
  /** Whether to hide the header completely */
  hideHeader?: boolean;
  /** Main panel content */
  children?: React.ReactNode;
  /** Footer content with status information */
  footer?: React.ReactNode;
  /** Whether to hide the footer completely */
  hideFooter?: boolean;

  /** Styling */
  /** Whether to show right border */
  borderRight?: boolean;
  /** Height behavior: 'screen' for 100vh, 'full' for 100% of parent */
  height?: "screen" | "full";
  /** Additional CSS classes */
  className?: string;
}

const sizeClasses = {
  auto: "flex-auto",
  "1": "flex-[0_0_8.333333%]",
  "2": "flex-[0_0_16.666667%]",
  "3": "flex-[0_0_25%]",
  "4": "flex-[0_0_33.333333%]",
  "5": "flex-[0_0_41.666667%]",
  "6": "flex-[0_0_50%]",
  "12": "flex-[0_0_100%]",
  full: "flex-1",
  fit: "flex-none",
};

const alignClasses = {
  start: "items-start",
  center: "items-center",
  end: "items-end",
  stretch: "items-stretch",
};

const justifyClasses = {
  start: "justify-start",
  center: "justify-center",
  end: "justify-end",
  between: "justify-between",
  around: "justify-around",
  evenly: "justify-evenly",
};

const getResponsiveClasses = (responsive?: PanelProps["responsive"]) => {
  if (!responsive) return "";

  const classes: string[] = [];

  if (responsive.sm) classes.push(`sm:${sizeClasses[responsive.sm]}`);
  if (responsive.md) classes.push(`md:${sizeClasses[responsive.md]}`);
  if (responsive.lg) classes.push(`lg:${sizeClasses[responsive.lg]}`);
  if (responsive.xl) classes.push(`xl:${sizeClasses[responsive.xl]}`);

  return classes.join(" ");
};

/**
 * Panel component for creating flexible layouts with header, content, and footer areas.
 *
 * @param props - Panel component props
 * @returns JSX element
 */
export function Panel({
  size = "auto",
  responsive,
  align = "stretch",
  justify = "start",
  title = "Panel",
  header,
  hideHeader = false,
  children,
  footer,
  hideFooter = false,
  borderRight = true,
  height = "full",
  className,
  ...props
}: PanelProps) {
  const baseSizeClass = sizeClasses[size];
  const responsiveClasses = getResponsiveClasses(responsive);

  // Determine height class based on height prop
  const heightClass = height === "screen" ? "h-screen" : "h-full";

  return (
    <div
      className={cn(
        "relative flex flex-col",
        heightClass,
        borderRight && "border-r border-border",
        baseSizeClass,
        responsiveClasses,
        alignClasses[align],
        justifyClasses[justify],
        className,
      )}
      {...props}
    >
      {!hideHeader && (
        <div className="absolute top-0 left-0 right-0 z-10 flex w-full h-12 px-3 py-2 justify-between items-center border-b border-border bg-background shadow-sm">
          {header ? (
            header
          ) : (
            <>
              <h6>{title}</h6>
              <Button variant="ghost" size="icon">
                <svg
                  className="size-4"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M12 5v.01M12 12v.01M12 19v.01M12 6a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2z"
                  />
                </svg>
              </Button>
            </>
          )}
        </div>
      )}

      {children && (
        <div
          className={cn(
            "flex-1 w-full p-3 items-start self-stretch bg-background overflow-y-auto",
            !hideHeader && "pt-12", // 48px for header + 12px spacing
            !hideHeader && "pt-[60px]", // 48px for header + 12px spacing
            hideHeader && "pt-0", // No top padding when header is hidden
            !hideFooter && footer && "pb-12", // Bottom padding only when footer exists and not hidden
            hideFooter && "pb-0", // No bottom padding when footer is hidden
          )}
        >
          {children}
        </div>
      )}

      {!hideFooter && footer && (
        <div className="absolute bottom-0 left-0 right-0 z-10 flex w-full h-12 min-h-10 px-3 py-2 justify-between items-center border-t border-border bg-background text-muted-foreground text-sm">
          {footer}
        </div>
      )}
    </div>
  );
}
