/* Button Utility Classes for v0 Compatibility */

/* Primary button */
.btn-primary {
  @apply inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-all disabled:pointer-events-none disabled:opacity-50 outline-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive;
  @apply bg-button-primary text-button-primary-foreground hover:bg-button-primary-hover;
}

/* Secondary button */
.btn-secondary {
  @apply inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-all disabled:pointer-events-none disabled:opacity-50 outline-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive;
  @apply bg-button-secondary border border-button-secondary-border text-button-secondary-foreground hover:bg-button-secondary-hover;
}

/* Outline button */
.btn-outline {
  @apply inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-all disabled:pointer-events-none disabled:opacity-50 outline-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive;
  @apply bg-button-outline border border-button-outline-border text-button-outline-foreground hover:bg-button-outline-hover;
}

/* Destructive button */
.btn-destructive {
  @apply inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-all disabled:pointer-events-none disabled:opacity-50 outline-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive;
  @apply bg-button-destructive text-button-destructive-foreground hover:bg-button-destructive-hover focus-visible:ring-destructive/20 dark:focus-visible:ring-destructive/40;
}

/* Ghost button */
.btn-ghost {
  @apply inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-all disabled:pointer-events-none disabled:opacity-50 outline-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive;
  @apply hover:bg-button-ghost-hover hover:text-button-ghost-hover-foreground;
}

/* Link button */
.btn-link {
  @apply inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-all disabled:pointer-events-none disabled:opacity-50 outline-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive;
  @apply text-button-link underline-offset-4 hover:text-button-link-hover hover:underline;
}

/* Size variants */
.btn-sm {
  @apply h-6 rounded-md gap-1.5 px-3;
}

.btn-lg {
  @apply h-10 rounded-md px-6;
}

.btn-icon {
  @apply size-8;
}

/* Icon support */
.btn-primary [&_svg],
.btn-secondary [&_svg],
.btn-outline [&_svg],
.btn-destructive [&_svg],
.btn-ghost [&_svg],
.btn-link [&_svg] {
  @apply pointer-events-none shrink-0;
}

.btn-primary [&_svg:not([class*='size-'])],
.btn-secondary [&_svg:not([class*='size-'])],
.btn-outline [&_svg:not([class*='size-'])],
.btn-destructive [&_svg:not([class*='size-'])],
.btn-ghost [&_svg:not([class*='size-'])],
.btn-link [&_svg:not([class*='size-'])] {
  @apply size-4;
}

/* Padding adjustments for buttons with icons */
.btn-primary:has(> svg),
.btn-secondary:has(> svg),
.btn-outline:has(> svg),
.btn-destructive:has(> svg),
.btn-ghost:has(> svg),
.btn-link:has(> svg) {
  @apply px-3;
}

.btn-sm:has(> svg) {
  @apply px-2.5;
}

.btn-lg:has(> svg) {
  @apply px-4;
}
