import { cn } from "@/lib/utils";
import type { HTMLAttributes } from "react";

interface PanelProps extends HTMLAttributes<HTMLDivElement> {
  children: React.ReactNode;
  className?: string;
}

/**
 * Panel component for creating flexible layouts.
 * 
 * This is a minimal panel component that provides basic styling
 * for panel layouts. The main Panel component is in @/components/panel.
 */
export function Panel({ className, children, ...props }: PanelProps) {
  return (
    <div
      className={cn(
        "relative flex flex-col bg-background border border-border rounded-lg",
        className
      )}
      {...props}
    >
      {children}
    </div>
  );
}
